cindx = char2ind(c);
if(cindx<1 || cindx>length(ctbl))
    cindx = char2ind('?');
end
img = reshape(dec2bin(hex2dec(ctbl{cindx}),8*5),[8,5])=='1';
switch(hw)
    case 75,img=img(1:7,1:5);
    case 45,img=img(1:4,1:5);
end
img(:,end+1)=0;
end